package com.yi2580.easypay.bean;import android.text.TextUtils;import java.util.Map;/** * 参数说明 * https://docs.open.alipay.com/204/105301 */public class AliPayResult {	private String resultStatus;	private String result;	private String memo;	public AliPayResult(Map<String, String> rawResult) {		if (rawResult == null) {			return;		}		for (String key : rawResult.keySet()) {			if (TextUtils.equals(key, "resultStatus")) {				resultStatus = rawResult.get(key);			} else if (TextUtils.equals(key, "result")) {				result = rawResult.get(key);			} else if (TextUtils.equals(key, "memo")) {				memo = rawResult.get(key);			}		}	}	@Override	public String toString() {		final StringBuffer sb = new StringBuffer("AliPayResult{");		sb.append("resultStatus='").append(resultStatus).append('\'');		sb.append(", result='").append(result).append('\'');		sb.append(", memo='").append(memo).append('\'');		sb.append('}');		return sb.toString();	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}	/**	 * {	 *     "memo" : "xxxxx",	 *     "result" : "{	 *                     \"alipay_trade_app_pay_response\":{	 *                         \"code\":\"10000\",	 *                         \"msg\":\"Success\",	 *                         \"app_id\":\"2014072300007148\",	 *                         \"out_trade_no\":\"081622560194853\",	 *                         \"trade_no\":\"2016081621001004400236957647\",	 *                         \"total_amount\":\"0.01\",	 *                         \"seller_id\":\"2088702849871851\",	 *                         \"charset\":\"utf-8\",	 *                         \"timestamp\":\"2016-10-11 17:43:36\"	 *                     },	 *                     \"sign\":\"NGfStJf3i3ooWBuCDIQSumOpaGBcQz+aoAqyGh3W6EqA/gmyPYwLJ2REFijY9XPTApI9YglZyMw+ZMhd3kb0mh4RAXMrb6mekX4Zu8Nf6geOwIa9kLOnw0IMCjxi4abDIfXhxrXyj********\",	 *                     \"sign_type\":\"RSA2\"	 *                 }",	 *     "resultStatus" : "9000"	 * }	 */}